INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1464604, 'Tolazamide', 'Miglitol', 'Moderate', 'Miglitol may potentiate the risk of hypoglycemia associated with the use of sulfonylureas or insulin. When administered alone, miglitol does not typically cause hypoglycemia in the fasted or postprandial state. However, because concomitant administration results in a further lowering of blood glucose, miglitol may increase the hypoglycemic potential of sulfonylureas and insulin.', 'DDInter', 1767317476, 'Dosage reduction of sulfonylureas and insulin should be considered when prescribed in combination with miglitol. Because miglitol inhibits the hydrolysis of sucrose to glucose and fructose, a source of glucose (dextrose, D-glucose) should be readily available to treat mild to moderate hypoglycemia. Severe hypoglycemia may require the use of either intravenous glucose infusion or glucagon injection', 'Synergism', 'Dosage reduction of sulfonylureas and insulin should be considered when prescribed in combination with miglitol.', NULL, NULL, 'Moderate', 'pharmacodynamic');
