INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236605, 'Abciximab', 'Alemtuzumab', 'Moderate', 'Coadministration of alemtuzumab with antiplatelet or anticoagulant drugs may increase the risk of bleeding.  Use of alemtuzumab alone has been associated with autoimmune and hemolytic anemia, thrombocytopenia, prolonged myelosuppression, bone marrow aplasia, hemophilia, and pancytopenia.  The mechanism for this interaction has not been clearly delineated, but is presumably due to additive antiplatelet/anticoagulant effects.', NULL, 'Caution is recommended if alemtuzumab is used concomitantly with antiplatelet or anticoagulant drugs.', NULL, 'Caution is recommended if alemtuzumab is used concomitantly with antiplatelet or anticoagulant drugs.  Patients should be monitored for increased anticoagulant effects and bleeding complications.  Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', 'Iloprost, Treprostinil, Defibrotide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Epoprostenol, Drotrecogin alfa', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/236841/', '[1] "Product Information. Campath (alemtuzumab)." Berlex Laboratories  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236606, 'Heparin (flush)', 'Abciximab', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', NULL, 'Caution and monitoring (e.', NULL, 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Acenocoumarol, Selexipag', 'Abciximab, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, Acetylcysteine, Nandrolone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/236848/', '[1] Passannante A, Macik BG "The heparin flush syndrome: a cause of iatrogenic hemorrhage." Am J Med Sci 296 (1988):  71-3[2] "Product Information. Heparin Sodium in Sodium Chloride (heparin flush)." Baxter Healthcare Corporation  (2022):[3] "Product Information. Canusal (heparin flush)." Wockhardt UK Ltd  (2018):[4] "Product Information. Heparinised Saline (Pfizer) (heparinised saline)." Pfizer Australia Pty Ltd  (2022):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236607, 'Amphotericin B (lipid complex)', 'Adefovir dipivoxil', 'Major', 'Coadministration of adefovir dipivoxil with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney.  Additionally, renal impairment secondary to the use of these agents may reduce the clearance of adefovir, which is primarily eliminated by renal excretion.  The use of adefovir dipivoxil has been associated with dose-related nephrotoxicity characterized by a delayed onset of gradual increases in serum creatinine and decreases in serum phosphorus.  Generally, the risk is low in patients with adequate renal function receiving 10 mg/day but increases with increasing dosage and in patients with underlying renal impairment.', NULL, 'Caution is advised if adefovir dipivoxil must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if adefovir dipivoxil must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).  Renal function should be evaluated prior to and during therapy with adefovir dipivoxil.  Patients with renal insufficiency at baseline or during treatment may require dosage adjustment in accordance with the manufacturer''s product labeling.', 'Excretion', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/237031/', '[1] "Product Information. Hepsera (adefovir)." Gilead Sciences  (2022):', 1767369485);
