INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236698, 'Fedratinib', 'Zafirlukast', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fedratinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19 and flavin-containing monooxygenase 3 (FMO3).', NULL, 'Caution is advised when fedratinib is used with CYP450 3A4 inhibitors, particularly those that may also inhibit CYP450 2C19 (e.', NULL, 'Caution is advised when fedratinib is used with CYP450 3A4 inhibitors, particularly those that may also inhibit CYP450 2C19 (e.g., amiodarone, cimetidine, fexinidazole, isoniazid, osilodrostat, rucaparib) or if combined with other drugs that can inhibit CYP450 2C19 (e.g., esomeprazole, fenofibrate, fluoxetine, lansoprazole, letrozole, moclobemide, omeprazole, parecoxib, ticlopidine).  Patients should be monitored for adverse reactions such as nausea, vomiting, diarrhea, anemia, thrombocytopenia, neutropenia, encephalopathy (including Wernicke''s), liver (ALT, AST) and pancreatic (amylase, lipase) enzyme elevations, increased blood creatinine, and secondary malignancies.  Dosage adjustment or treatment interruption may be required based on patient tolerance and response in accordance with the prescribing information.', 'Metabolism', 'Dacomitinib, Pacritinib, Vemurafenib, Vandetanib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, More', 'Theophylline, Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238122/', '[1] Wu F, Krishna