INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236759, 'Rabeprazole', 'Osilodrostat', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', NULL, 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Tetracycline, Sucralfate, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Ranitidine, Amoxicillin, Lansoprazole, Ketoprofen, Diclofenac, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238576/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc  (2020):[2] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Australia Pty Ltd  (2022):[3] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases UK Ltd  (2021):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236760, 'Rabeprazole', 'Pemigatinib', 'Moderate', 'Coadministration of pemigatinib with proton pump inhibitors may result in a reduction in the plasma concentrations of pemigatinib.  The mechanism for this interaction has not been delineated.', NULL, 'Until more information is available, caution is recommended when pemigatinib is used concomitantly with proton pump inhibitors.', NULL, 'Until more information is available, caution is recommended when pemigatinib is used concomitantly with proton pump inhibitors.  Patients should be monitored for potentially reduced efficacy.', 'Others', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, Cobimetinib, More', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Nizatidine, Ranitidine, Amoxicillin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238577/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236761, 'Rabeprazole', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, Cobimetinib, More', 'Tetracycline, Sucralfate, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Ranitidine, Amoxicillin, Fenoprofen, Meclofenamic acid, Ketorolac, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238579/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics.