INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236764, 'Adefovir dipivoxil', 'Metformin', 'Moderate', 'Theoretically, coadministration of adefovir dipivoxil with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of adefovir and/or the coadministered drug(s).  The mechanism is competitive inhibition of renal excretion.  Drugs that are thought to undergo active tubular secretion include metformin, cimetidine, ranitidine, procainamide, flecainide, quinidine, triamterene, midodrine, cidofovir, acyclovir, valacyclovir, tenofovir, ganciclovir, and valganciclovir.', NULL, 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', 'Cabotegravir, Maraviroc, Zanamivir, Lenacapavir, Telaprevir, Rilpivirine, Etravirine, Cobicistat, Brincidofovir, Remdesivir, Doravirine, More', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Tolbutamide, Miglitol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238607/', '[1] "Product Information. Hepsera (adefovir)." Gilead Sciences  (2022):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236765, 'Fedratinib', 'Metformin', 'Moderate', 'Coadministration with fedratinib may decrease the renal clearance of drugs that are excreted via organic cation transporter (OCT) 2 and multidrug and toxin extrusion (MATE) 1/2-K.', NULL, 'Caution is advised when fedratinib is used with drugs that are renally excreted via OCT2, MATE1, and MATE2-K.', NULL, 'Caution is advised when fedratinib is used with drugs that are renally excreted via OCT2, MATE1, and MATE2-K.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fedratinib is added to or withdrawn from therapy, particularly those with a narrow therapeutic index.', 'Excretion', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, More', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Miglitol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238612/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Inrebic (fedratinib)." Celgene Corporation  (2019):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236766, 'Fluocinolone acetonide', 'Metformin', 'Minor', 'The efficacy of insulin and other antidiabetic agents may be diminished by topical corticosteroids, particularly during prolonged or indiscriminate use.  Corticosteroids can raise blood glucose level by antagonizing the action and suppressing the secretion of insulin, which results in inhibition of peripheral glucose uptake and increased gluconeogenesis.', NULL, 'Systemic absorption of topical corticosteroids may occur depending on the vehicle and concentration of the preparation, the size of the application area, the integrity of the skin, and the duration of administration.', NULL, 'Systemic absorption of topical corticosteroids may occur depending on the vehicle and concentration of the preparation, the size of the application area, the integrity of the skin, and the duration of administration.  Use of occlusive dressings over the applied areas may also increase percutaneous absorption.  Given equivalent doses, small children are generally at the greatest risk because of their larger skin surface to body mass ratios.  If possible, the use of highly potent agents (e.g., augmented betamethasone, clobetasol, diflorasone, and halobetasol) should be avoided in children and limited to small areas for 2 weeks in adults.  Close clinical monitoring of glycemic control is recommended if topical corticosteroids are administered chronically and/or to large areas in diabetic patients.', 'Antagonism', 'Fluorometholone, Hydrocortisone butyrate, Tetracaine, Zinc sulfate, Zinc acetate, Fluocinonide, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate, Salicylic acid, More', 'Simvastatin, Guar gum, Rosuvastatin', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238614/', '[1] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986):  139-42[2] Ludvik B, Clodi M, Kautzky-Willer A, Capek M, Hartter E, Pacini G, Prager R "Effect of dexamethasone on insulin sensitivity, islet amyloid polypeptide and insulin secretion in humans." Diabetologia 36 (1993):  84-7[3] Carruthers JA, Staughton RC, August PJ "Penetration of topical steroid preparations." Arch Dermatol 113 (1977):  522[4] Pace WE "Topical corticosteroids." Can Med Assoc J 108 (1973):  11 passim[5] "Product Information. Ultravate (halobetasol topical)." Apothecon Inc  (2022):[6] "Product Information. Diprolene (betamethasone topical)." Schering Corporation  (2001):[7] "Product Information. Temovate (clobetasol topical)." Glaxo Wellcome[8] "Product Information. Psorcon (diflorasone topical)." Rhone Poulenc Rorer  (2001):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236767, 'Pralsetinib', 'Metformin', 'Moderate', 'Coadministration with pralsetinib may increase the plasma concentrations of drugs that are P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic anion transporting polypeptide 1B1 (OATP1B1), OATP1B3, organic anion transporter 1 (OAT1), multidrug and toxin extrusion 1 (MATE1), and/or MATE2-K substrates.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with substrates of these transporters for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism, Excretion', 'Lixisenatide, Albiglutide, Guar gum, Acarbose, Pramlintide, Canagliflozin, Alogliptin, Liraglutide, Troglitazone, Ertugliflozin, Semaglutide, More', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238627/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236768, 'Selpercatinib', 'Metformin', 'Minor', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the renal transporter multidrug and toxin extrusion protein 1 (MATE1).', NULL, 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the renal transporter multidrug and toxin extrusion protein 1 (MATE1).', NULL, 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the renal transporter multidrug and toxin extrusion protein 1 (MATE1).  In vitro studies indicate that selpercatinib inhibits MATE1 at clinically relevant concentrations.  However, the clinical significance of this finding has not been established.  No clinically significant differences in glucose levels were observed when metformin, a MATE1 substrate, was coadministered with selpercatinib.', 'Excretion', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/238628/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company  (2020):', 1767369485);
