INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236817, 'Ibuprofen', 'Heparin (flush)', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', NULL, 'Caution and monitoring (e.', NULL, 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Protein C, Selexipag, Pentosan polysulfate, Vitamin A, Ascorbic acid, Nandrolone', 'Ritodrine, Bremelanotide, Flibanserin, Cabergoline, Black cohosh, Bromocriptine, Ranolazine, Ubidecarenone, Ivabradine, Adenosine, Regadenoson, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239551/', '[1] Passannante A, Macik BG "The heparin flush syndrome: a cause of iatrogenic hemorrhage." Am J Med Sci 296 (1988):  71-3[2] "Product Information. Heparin Sodium in Sodium Chloride (heparin flush)." Baxter Healthcare Corporation  (2022):[3] "Product Information. Canusal (heparin flush)." Wockhardt UK Ltd  (2018):[4] "Product Information. Heparinised Saline (Pfizer) (heparinised saline)." Pfizer Australia Pty Ltd  (2022):', 1767369485);
