INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236860, 'Nortriptyline', 'Medroxyprogesterone acetate', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy.  Simultaneous TCA toxicity and reduced effects have been reported.  Akathisia has also been reported in some women taking this combination.  The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', NULL, 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy.', NULL, 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy.  Simultaneous TCA toxicity and reduced effects have been reported.  Akathisia has also been reported in some women taking this combination.  The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.  The clinical significance of this interaction has not been established.  Monitoring for altered effects may be advisable during concomitant therapy.  Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Estetrol, More', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Esketamine, Phenelzine, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239641/', '[1] Abernethy DR, Greenblatt DJ, Shader RI "Imipramine disposition 