INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236863, 'Pomalidomide', 'Medroxyprogesterone acetate', 'Major', 'Concomitant treatment with agents that can cause thrombosis such as erythropoiesis-stimulating agents, estrogens, selective estrogen receptor modulators, or C1 esterase inhibitors may potentiate the risk of venous thromboembolic events associated with the use of pomalidomide.', NULL, 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving pomalidomide.', NULL, 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving pomalidomide.  Prophylactic antithrombotic measures should be considered after assessing underlying risk factors such as health history and concomitant medications.  Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Etynodiol, Levonorgestrel, Desogestrel, Etonogestrel, Norgestrel, Megestrol acetate, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Methotrexate, Abatacept, Upadacitinib, Siponimod, Apremilast, Inebilizumab, Belumosudil, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239647/', '[1] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc  (2013):', 1767369485);
