INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236868, 'Ropinirole', 'Medroxyprogesterone acetate', 'Moderate', 'Coadministration with estrogen-containing drugs may increase the plasma concentrations of ropinirole.The mechanism of interaction has not been described.', NULL, 'Dosage adjustment for ropinirole should not be necessary in patients already on estrogen therapy because patients must be carefully titrated to tolerance or adequate effect.', NULL, 'Dosage adjustment for ropinirole should not be necessary in patients already on estrogen therapy because patients must be carefully titrated to tolerance or adequate effect.  However, if estrogen therapy is initiated or discontinued during treatment with ropinirole, then patients should be monitored for altered ropinirole effects and the dosage adjusted as necessary.  Patients should be advised to notify their physician if they experience agitation, orthostasis, sedation, confusion, hallucinations, and/or increased dyskinesia, flushing, dry mouth, sweating, or heart rate.', 'Others', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Etonogestrel, More', 'Apomorphine, Opicapone, Cabergoline, Amantadine, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Bromocriptine, Pergolide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239652/', '[1] "Product Information. Requip (ropinirole)." SmithKline Beecham  (2001):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236869, 'Saw palmetto', 'Medroxyprogesterone acetate', 'Minor', 'Theoretically, coadministration with saw palmetto may interfere with the pharmacologic effects of estrogen-containing drugs including oral contraceptives.  Saw palmetto has been observed in one study to possess antiestrogenic effects.  However, the interaction has not been reported.', NULL, 'Try to avoid taking saw palmetto and estrogenic medications at the same time to avoid unexpected medical events.', NULL, 'Try to avoid taking saw palmetto and estrogenic medications at the same time to avoid unexpected medical events.', 'Antagonism', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Estetrol, More', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239653/', '[1] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998):  2200-11', 1767369485);
