INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236888, 'Adefovir dipivoxil', 'Ketorolac', 'Major', 'Coadministration of adefovir dipivoxil with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney.  Additionally, renal impairment secondary to the use of these agents may reduce the clearance of adefovir, which is primarily eliminated by renal excretion.  The use of adefovir dipivoxil has been associated with dose-related nephrotoxicity characterized by a delayed onset of gradual increases in serum creatinine and decreases in serum phosphorus.  Generally, the risk is low in patients with adequate renal function receiving 10 mg/day but increases with increasing dosage and in patients with underlying renal impairment.', NULL, 'Caution is advised if adefovir dipivoxil must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if adefovir dipivoxil must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).  Renal function should be evaluated prior to and during therapy with adefovir dipivoxil.  Patients with renal insufficiency at baseline or during treatment may require dosage adjustment in accordance with the manufacturer''s product labeling.', 'Excretion', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, Phenylephrine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239715/', '[1] "Product Information. Hepsera (adefovir)." Gilead Sciences  (2022):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236889, 'Alemtuzumab', 'Ketorolac', 'Moderate', 'Coadministration of alemtuzumab with drugs that have antiplatelet or anticoagulant effects, such as nonsteroidal anti-inflammatory drugs (NSAIDs), may increase the risk of bleeding.  Use of alemtuzumab alone has been associated with autoimmune and hemolytic anemia, thrombocytopenia, prolonged myelosuppression, bone marrow aplasia, hemophilia, and pancytopenia.  The mechanism for this interaction has not been clearly delineated, but is presumably due to additive antiplatelet/anticoagulant effects.', NULL, 'Caution is recommended if alemtuzumab is used concomitantly with NSAIDS.', NULL, 'Caution is recommended if alemtuzumab is used concomitantly with NSAIDS.  Patients should be monitored for increased anticoagulant effects and bleeding complications.  Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib, Homatropine, Atropine, Scopolamine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239716/', '[1] "Product Information. Campath (alemtuzumab)." Berlex Laboratories  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236890, 'Ginkgo biloba', 'Ketorolac', 'Moderate', 'Ginkgo may potentiate the risk of bleeding associated with anticoagulants, platelet inhibitors, nonsteroidal anti-inflammatory drugs (NSAIDs), and thrombolytic agents.  Ginkgolide B, a component of ginkgo, inhibits platelet-activating factor by displacing it from its receptor-binding site, resulting in reduced platelet aggregation.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.  In general, consumption of ginkgo should be avoided during use of coagulation-modifying agents and at least two weeks prior to surgery.  In patients who have used this herb extensively prior to receiving anticoagulation, antiplatelet, NSAID or thrombolytic therapy, the potential for an interaction should be considered.  Close clinical and laboratory observation for hematologic complications is recommended.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Rivastigmine, Galantamine, Aducanumab, Memantine, Tacrine', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Homatropine, Atropine, Scopolamine, Methscopolamine, Tropicamide, Phenylephrine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/239724/'