INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236917, 'Pyrophosphoric acid', 'Nifedipine', 'Moderate', 'Medicinal products including sodium heparin, prazosin, methyldopa, hydralazine, quinidine, beta blockers, calcium channel blockers, digitalis glycosides, nitrates, anthracyclines, and iodinated contrast agents may interfere with the blood pool images taken using Technetium Tc 99m pyrophosphate.  This is characterized by a reduction in the amount of injected radioactivity remaining in the blood pool.', NULL, 'Clinicians should be aware of the possibility of a diagnostic interference.', NULL, 'Clinicians should be aware of the possibility of a diagnostic interference.  When use of iodinated contrast media is anticipated, it is recommended to perform the scintigraphy with (99mTc)-labeled red blood cells prior to administration of the iodinated contrast media to avoid adversely affecting the red blood cell labeling.', 'Distribution', 'Mibefradil, Celecoxib, Acetylsalicylic acid, Ivabradine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240608/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Pyrophosphate (pyrophosphate)." Pharmalucence Inc  (2012):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236918, 'Remimazolam', 'Nifedipine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Celecoxib, Acetylsalicylic acid, Ivabradine', 'Chloral hydrate, Flurazepam, Quazepam, Paraldehyde, Eszopiclone, Ramelteon, Scopolamine, Estazolam, Ethchlorvynol, Tasimelteon, Melatonin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240611/', '[1] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991):  390-1[2] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991):  139[3] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990)