INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236946, 'Human botulinum neurotoxin A/B immune globulin', 'Adefovir dipivoxil', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).  The manufacturers recommend administering immune globulin infusions at the minimum concentration available and at the minimum rate of infusion feasible in such patients.  Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy.  Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.  If renal function deteriorates, discontinuation of the product should be considered.  Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240960/', '[1] "Product Information. Cytogam (cytomegalovirus immune globulin)." CSL Behring LLC  (2002):[2] "Product Information. Respigam (respiratory syncytial virus immune globulin)." Medimmune Inc  (2005):[3] "Product Information. BabyBIG (botulism immune globulin)." FFF Enterprises  (2008):[4] "Product Information. Bivigam (immune globulin intravenous)." Biotest Pharmaceuticals Corporation  (2013):[5] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998.  http://www.cdc.gov/mmwr/preview/mmwrhtml/mm4824a3.htm"   (2013):[6] Pusey EY, Levy JB "Nephrotoxicity of intravenous immunoglobulin.  http://qjmed.oxfordjournals.org/content/93/11/751.full.pdf+html"   (2013):[7] KDIGO. Kidney Disease Improving Global Outcomes "KDIGO clinical practice guideline for acute kidney injury.  http://www.kdigo.org/clinical_practice_guidelines/pdf/KDIGO-AKI-Suppl-Appendices-A-F_March2012.pdf"   (2013):[8] Guo X, Nzerue C "How to prevent, recognize, and treat drug-induced nephrotoxicity.  http://www.ccjm.org/content/69/4/289.full.pdf"   (2013):[9] Perazella MA "Renal vulnerability to drug toxicity.  http://www.ccjm.org/content/69/4/289.full.pdf"   (2013):[10] Naughton CA "Drug-induced nephrotoxicity.  http://www.aafp.org/afp/2008/0915/p743.html"   (2013):[11] Moses S "Nephrotoxic drugs, drug-induced nephrotoxicity.  http://www.fpnotebook.com/Renal/Pharm/NphrtxcDrgs.htm"   (2013):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236947, 'Adefovir dipivoxil', 'Bromfenac', 'Major', 'Coadministration of adefovir dipivoxil with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney.  Additionally, renal impairment secondary to the use of these agents may reduce the clearance of adefovir, which is primarily eliminated by renal excretion.  The use of adefovir dipivoxil has been associated with dose-related nephrotoxicity characterized by a delayed onset of gradual increases in serum creatinine and decreases in serum phosphorus.  Generally, the risk is low in patients with adequate renal function receiving 10 mg/day but increases with increasing dosage and in patients with underlying renal impairment.', NULL, 'Caution is advised if adefovir dipivoxil must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if adefovir dipivoxil must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).  Renal function should be evaluated prior to and during therapy with adefovir dipivoxil.  Patients with renal insufficiency at baseline or during treatment may require dosage adjustment in accordance with the manufacturer''s product labeling.', 'Excretion', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 'Salicylic acid, Fluocinolone acetonide, Cortisone, Hydrocortisone, Betamethasone, Fluorometholone, Triamcinolone, Dexamethasone', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240961/', '[1] "Product Information. Hepsera (adefovir)." Gilead Sciences  (2022):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (236948, 'Adefovir dipivoxil', 'Cabozantinib', 'Moderate', 'Coadministration of cabozantinib with multidrug resistance-associated proteins (MRP2) inhibitors may increase exposure and toxicity of cabozantinib.', NULL, 'Caution is advised if cabozantinib is used concomitantly with a MRP2 inhibitor.', NULL, 'Caution is advised if cabozantinib is used concomitantly with a MRP2 inhibitor.  Monitor for cabozantinib toxicity, such as gastrointestinal perforations and fistulas, hemorrhage, thrombotic events, hypertension, diarrhea, and palmar-plantar erythrodysesthesia.  Dose reductions, interruption or discontinuation of cabozantinib may be necessary if toxicity occurs.', 'Distribution', 'Paritaprevir, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/240962/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Cometriq (cabozantinib)." Exelixis Inc  (2012):[4] "Product Information. Cabometyx (cabozantinib)." Exelixis Inc  (2016):', 1767369485);
