INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1465704, 'Neomycin', 'Lactulose', 'Minor', 'On theoretical grounds and based on limited experimental data in rats, coadministration with neomycin may presumably reduce the effectiveness of lactulose in the treatment of portal-systemic encephalopathy by eliminating certain colonic bacteria necessary for the degradation of lactulose to organic acids. However, the relevance of this interaction has not been fully elucidated. Clinical data do not appear to support a significant effect of neomycin on lactulose-induced reduction of colonic pH, and concomitant use has resulted in positive effects on various therapeutic markers of portal-systemic encephalopathy.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
