INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1465797, 'Digitoxin', 'Milnacipran', 'Moderate', 'Use of milnacipran concomitantly with digoxin may be associated with potentiation of adverse hemodynamic effects. Inhibition of norepinephrine and serotonin reuptake by milnacipran can lead to cardiovascular effects including sustained increases in blood pressure and heart rate. However, effects of milnacipran in patients with significant hypertension, arrhythmia, or cardiac disease have not been systematically evaluated.', 'DDInter', 1767317476, 'Coadministration of milnacipran and intravenous digoxin should be avoided. Caution is advised during coadministration with oral digoxin therapy. Blood pressure and heart rate should be measured prior to initiating milnacipran and periodically throughout treatment. A dosage reduction or discontinuation should be considered in patients who experience a sustained increase in blood pressure or heart rate while receiving milnacipran.', 'Synergism', 'Coadministration of milnacipran and intravenous digoxin should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1465798, 'Dobutamine', 'Milnacipran', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', 'DDInter', 1767317476, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
