INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237001, 'Adefovir dipivoxil', 'Naproxen', 'Major', 'Coadministration of adefovir dipivoxil with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney.  Additionally, renal impairment secondary to the use of these agents may reduce the clearance of adefovir, which is primarily eliminated by renal excretion.  The use of adefovir dipivoxil has been associated with dose-related nephrotoxicity characterized by a delayed onset of gradual increases in serum creatinine and decreases in serum phosphorus.  Generally, the risk is low in patients with adequate renal function receiving 10 mg/day but increases with increasing dosage and in patients with underlying renal impairment.', NULL, 'Caution is advised if adefovir dipivoxil must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if adefovir dipivoxil must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).  Renal function should be evaluated prior to and during therapy with adefovir dipivoxil.  Patients with renal insufficiency at baseline or during treatment may require dosage adjustment in accordance with the manufacturer''s product labeling.', 'Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241021/', '[1] "Product Information. Hepsera (adefovir)." Gilead Sciences  (2022):', 1767363027);
