INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237016, 'Adefovir dipivoxil', 'Procainamide', 'Moderate', 'Theoretically, coadministration of adefovir dipivoxil with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of adefovir and/or the coadministered drug(s).  The mechanism is competitive inhibition of renal excretion.  Drugs that are thought to undergo active tubular secretion include metformin, cimetidine, ranitidine, procainamide, flecainide, quinidine, triamterene, midodrine, cidofovir, acyclovir, valacyclovir, tenofovir, ganciclovir, and valganciclovir.', NULL, 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241036/', '[1] "Product Information. Hepsera (adefovir)." Gilead Sciences  (2022):', 1767363027);
