INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237146, 'Bupropion', 'Oxymetazoline (nasal)', 'Moderate', 'Translated with DeepL', NULL, 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.', NULL, 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.  Blood pressure and heart rate should be measured prior to initiating bupropion therapy and monitored at regular intervals consistent with usual clinical practice, particularly in patients with preexisting hypertension.  Dose reduction or discontinuation of bupropion should be considered in patients who experience clinically significant and sustained increases in blood pressure or heart rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/241953/', '[1] "Product Information. Zyban (bupropion)." GlaxoSmithKline UK Ltd  (2022):[2] "Product Information. Auvelity (bupropion-dextromethorphan)." Axsome Therapeutics, Inc.  (2022):[3] "Product Information. Wellbutrin XL (bupropion)." Bausch Health, Canada Inc.  (2022):[4] "Product Information. Contrave (bupropion-naltrexone)." Currax Pharmaceuticals LLC  (2021):', 1767363027);
