INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237154, 'Anagrelide', 'Pralsetinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/242013/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237155, 'Anagrelide', 'Relugolix', 'Major', 'Anagrelide can cause dose-dependent prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided.  Patients receiving anagrelide should have a cardiovascular examination, including an ECG, prior to initiation of treatment and monitored for cardiovascular effects during treatment.  Hypokalemia or hypomagnesemia must be corrected prior to therapy and electrolytes monitored periodically during therapy.  Anagrelide should not be used in the presence of known risk factors for QT interval prolongation such as congenital long QT syndrome or a history of acquired QTc prolongation.  Periodic electrocardiograms are recommended in patients with heart failure, bradyarrhythmias, or electrolyte abnormalities.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/242014/', '[1] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363027);
