INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237343, 'Fexofenadine', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may increase the plasma concentrations of drugs that are P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic anion transporting polypeptide 1B1 (OATP1B1), OATP1B3, organic anion transporter 1 (OAT1), multidrug and toxin extrusion 1 (MATE1), and/or MATE2-K substrates.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with substrates of these transporters for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243003/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237344, 'Fexofenadine', 'Relugolix', 'Moderate', 'Concomitant use with relugolix may lead to increased plasma concentrations of substrates of the intestinal efflux transporter, P-glycoprotein (P-gp).  According to the manufacturer, a relugolix dose of 40 mg may lead to saturation of intestinal P-gp.  In addition, relugolix exhibits more than dose-proportional pharmacokinetics over a dose range of 10 mg to 120 mg, which may lead to increased absorption of coadministered medicines that are sensitive substrates of P-gp.', NULL, 'Some authorities advise that the coadministration of relugolix with sensitive substrates of P-glycoprotein, such as dabigatran etexilate, should be avoided.', NULL, 'Some authorities advise that the coadministration of relugolix with sensitive substrates of P-glycoprotein, such as dabigatran etexilate, should be avoided.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243004/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc.  (2021):[3] "Product Information. Myfembree (estradiol/norethindrone/relugolix)." Myovant Sciences, Inc.  (2021):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237345, 'Fexofenadine', 'Risdiplam', 'Moderate', 'Coadministration with risdiplam may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of multidrug and toxin extrusion 1 and 2K (MATE1 and MATE2K) proteins.  Based on in vitro data, risdiplam and its metabolite are MATE1 and MATE2K transport inhibitors.  The proposed mechanism is decreased clearance due to risdiplam-mediated inhibition of MATE1 and/or MATE2K.', NULL, 'Coadministration of risdiplam with drugs that are substrates of MATE1 and/or MATE2K should generally be avoided.', NULL, 'Coadministration of risdiplam with drugs that are substrates of MATE1 and/or MATE2K should generally be avoided.  However, if concomitant use is unavoidable, monitor for drug-related toxicities and consider dosage reduction of the MATE1 or MATE2K substrate (based in the labeling of that drug) as needed.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243005/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Evrysdi (risdiplam)." Genentech  (2020):[3] Yonezawa A, Inui K "Importance of the multidrug and toxin extrusion MATE/SLC47A family to pharmacokinetics, pharmacodynamics/toxicodynamics and pharmacogenomics." Br J Pharmacol 164 (2011):  1817-25', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237346, 'Fexofenadine', 'Selpercatinib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter.  The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by selpercatinib.', NULL, 'Caution is advised when selpercatinib is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when selpercatinib is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy.', 'Metabolism, Absorption, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243006/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company  (2020):', 1767363027);
