INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237396, 'Pralsetinib', 'Palonosetron', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243173/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237397, 'Alpelisib', 'Berotralstat', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat.  Berotralstat is a substrate of both P-gp and BCRP.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.  Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea.  Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243178/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc  (2021):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237398, 'Eslicarbazepine', 'Alpelisib', 'Moderate', 'Coadministration of alpelisib with inducers of CYP450 3A4 may reduce the plasma concentrations of alpelisib.  The proposed mechanism, based on in vitro data, is increased clearance due to induction of CYP450 3A4, which is one of the routes of metabolism of alpelisib.', NULL, 'Caution is recommended when alpelisib is used with CYP450 3A4 inducers.', NULL, 'Caution is recommended when alpelisib is used with CYP450 3A4 inducers.  Pharmacologic effects of alpelisib should be monitored more closely whenever the inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243183/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals  (2019):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237399, 'Alpelisib', 'Relugolix', 'Moderate', 'Coadministration with alpelisib may theoretically increase the plasma concentrations of drugs that are substrates of organic anion transporter (OAT3), intestinal breast cancer resistance protein (BCRP), and/or P-glycoprotein (P-gp).  The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by alpelisib and/or its metabolite (BZG791).', NULL, 'Caution is advised if alpelisib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if alpelisib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever alpelisib is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243195/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals  (2019):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237400, 'Alpelisib', 'Tivozanib', 'Moderate', 'Coadministration of inhibitors of the breast cancer resistance protein (BCRP) transporter with alpelisib may increase the plasma concentrations and risk of adverse effects of alpelisib, which is a substrate of this efflux transporter.', NULL, 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia.', NULL, 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia.  Some authorities recommend coadministration of alpelisib with BCRP inhibitors be avoided whenever possible (US).', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243202/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals  (2019):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237401, 'Vitamin E', 'Axicabtagene ciloleucel', 'Moderate', 'The potential effects of vitamin E on cancer chemotherapy and radiation have not been established.  Because vitamin E is an antioxidant, pharmacologic doses could theoretically interfere with radiation therapy and chemotherapeutic agents whose cytotoxic mechanism depends on generation of reactive oxygen species (ROS) that damage DNA and proteins, including but not limited to alkylating agents (e.g., busulfan, cyclophosphamide, melphalan), anthracyclines (e.g., doxorubicin, epirubicin), platinum coordination complexes (e.g., cisplatin, carboplatin), DNA topoisomerase inhibitors (e.g., etoposide, teniposide, irinotecan, topotecan), and photodynamic agents (e.g., porfimer).  On the other hand, vitamin E may help reduce oxidative stress associated with more aggressive cancers and protect non-cancer cells from oxidative damage generated by cancer treatments, which can enhance patient tolerance and lessen the need for reducing dosage or duration of treatment.', NULL, 'Until more information is available, vitamin E supplementation should preferably be avoided in patients undergoing cancer chemotherapy or radiation treatment.', NULL, 'Until more information is available, vitamin E supplementation should preferably be avoided in patients undergoing cancer chemotherapy or radiation treatment.  Clinicians should closely monitor antitumor response if vitamin E is used in these patients.  No specific dosing of vitamin E has been established for uses other than to treat vitamin E deficiency.  There has been evidence suggesting possible adverse health effects including increased risk of heart failure and mortality in association with long-term use of 400 IU/day or greater.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243205/', '[1] Whittaker JA, Al-Ismail SA "Effect of digoxin and vitamin E in preventing cardiac damage caused by doxorubicin in acute myeloid leukaemia." Br Med J (Clin Res Ed) 288 (1984):  283-4[2] Lonn E, Bosch J, Yusuf S, et al. "Effects of long-term vitamin E supplementation on cardiovascular events and cancer: a randomized controlled trial." JAMA 293 (2005):  1338-47[3] Labriola D, Livingston R "Possible interactions between dietary antioxidants and chemotherapy." Oncology (Williston Park) 13 (1999):  1003-8; discussion 1008, 1011-2[4] Therapeutic Research Faculty "Natural Medicines Comprehensive Database.  http://www.naturaldatabase.com"   (2008):[5] Block KI, Koch AC, Mead MN, Tothy PK, Newman RA, Gyllenhaal C "Impact of antioxidant supplementation on chemotherapeutic toxicity: a systematic  review of the evidence from randomized controlled trials." Int J Cancer 123 (2008):  1227-39[6] D''Andrea GM "Use of antioxidants during chemotherapy and radiotherapy should be avoided." CA Cancer J Clin 55 (2005):  319-21[7] Prasad KN "Rationale for using high-dose multiple dietary antioxidants as an adjunct to radiation therapy and chemotherapy." J Nutr 134 (2004):  3182S-3S[8] Conklin KA "Cancer chemotherapy and antioxidants." J Nutr 134 (2004):  3201S-4S[9] Pace A, Savarese A, Picardo M, et al. "Neuroprotective effect of vitamin E supplementation in patients treated with cisplatin chemotherapy." J Clin Oncol 21 (2003):  927-31', 1767363027);
