INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237485, 'Tivozanib', 'Sulfasalazine', 'Moderate', 'Coadministration with tivozanib may increase the plasma concentrations and therapeutic effects of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.  The proposed mechanism, based on in vitro data, is decreased clearance due to inhibition of BCRP-mediated intestinal efflux by tivozanib.', NULL, 'Caution and clinical monitoring are advised if tivozanib is prescribed with orally administered drugs that are sensitive substrates for BCRP, such as rosuvastatin.', NULL, 'Caution and clinical monitoring are advised if tivozanib is prescribed with orally administered drugs that are sensitive substrates for BCRP, such as rosuvastatin.  If coadministration is necessary, the manufacturer suggests separating the administration times by 2 or more hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/243592/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Fotivda (tivozanib)." Aveo Pharmaceuticals, Inc.  (2021):', 1767363027);
