        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1466176, 'Tadalafil', 'Nebivolol', 'Minor', 'Based on their pharmacology, phosphodiesterase-5 (PDE5) inhibitors may conceivably potentiate the hypotensive effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
