    ) VALUES (1466451, 'Sevoflurane', 'Nefazodone', 'Moderate', 'There are no data on the safety of administering general anesthetics to patients concurrently on nefazodone.', NULL, 'The manufacturer recommends discontinuing nefazodone for as long as possible before elective surgery.', NULL, 'The manufacturer recommends discontinuing nefazodone for as long as possible before elective surgery.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
