    ) VALUES (1466480, 'Terfenadine', 'Nefazodone', 'Major', 'Nefazodone has been shown in vitro to inhibit CYP450 3A4, the isoenzyme responsible for the metabolism of terfenadine. Accumulation of terfenadine following administration of other drugs that inhibit CYP450 3A4 has resulted in prolonged QT interval and, rarely, serious arrhythmias and death.', NULL, 'Although there have been no clinical reports of adverse events related to the coadministration of nefazodone and terfenadine, the manufacturer of nefazodone considers this combination to be contraindicated.', NULL, 'Although there have been no clinical reports of adverse events related to the coadministration of nefazodone and terfenadine, the manufacturer of nefazodone considers this combination to be contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
