INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237592, 'Botulinum toxin type A', 'Protriptyline', 'Moderate', 'Use of anticholinergic drugs after administration of botulinum toxin may potentiate systemic anticholinergic effects such as dry mouth, blurred vision, and urinary disorders.  Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive or synergistic anticholinergic effects may occur when these agents are used together.', NULL, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.', NULL, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) are used after administration of botulinum toxin.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/244117/', '[1] "Product Information. Myobloc (rimabotulinumtoxinB)." Elan Pharmaceuticals[2] "Product Information. Dysport (abobotulinumtoxinA)." Tercica Inc[3] "Product Information. Botox (onabotulinumtoxinA)." Allergan Inc[4] "Product Information. Xeomin (incobotulinumtoxinA)." Merz Pharmaceuticals  (2022):[5] "Product Information. Jeuveau (prabotulinumtoxinA)." Evolus, Inc.  (2022):[6] "Product Information. Daxxify (daxibotulinumtoxinA)." Revance Therapeutics, Inc.  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237593, 'Protriptyline', 'Pralsetinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/244121/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767365210);
