INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237601, 'Glycerol phenylbutyrate', 'Vorinostat', 'Moderate', 'Coadministration of histone deacetylase (HDAC) inhibitors and phenylbutyrate may lead to excess inhibition of HDAC and additive adverse effects.  According to the prescribing information for sodium phenylbutyrate combined with taurursodiol (ursodoxicoltaurine), phenylbutyrate is a pan-HDAC inhibitor', NULL, 'The prescribing information for sodium phenylbutyrate combined with taurursodiol recommends avoiding concomitant use of phenylbutyrate and other HDAC inhibitors.', NULL, 'The prescribing information for sodium phenylbutyrate combined with taurursodiol recommends avoiding concomitant use of phenylbutyrate and other HDAC inhibitors.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245215/', '[1] "Product Information. Relyvrio (sodium phenylbutyrate-taurursodiol)." Amylyx Pharmaceuticals  (2022):[2] "Product Information. Albrioza (sodium phenylbutyrate-ursodoxicoltaurine)." Innomar Strategies Inc.  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237602, 'Vorinostat', 'Samarium (153Sm) lexidronam', 'Major', 'Theoretical concerns exist that chemotherapeutic agents and other bone marrow depressants may potentiate the myelosuppressive effects of samarium sm 153 lexidronam.  In clinical trials, white blood cell and platelet counts decreased to a nadir of approximately 40% to 50% of baseline in 95% of patients within three to five weeks after administration of samarium sm 153 lexidronam, and tended to return to pretreatment levels by eight weeks.  The potential for additive bone marrow toxicity with myelotoxic treatments including chemotherapy or external beam radiation has not been studied.', NULL, 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks.', NULL, 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks.  Moreover, samarium sm 153 lexidronam should not be given after either of these treatments until there has been time for adequate marrow recovery.  Caution and close monitoring of bone marrow function are advisable if coadministration with other myelotoxic agents is required.  Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, unusual bleeding or bruising, or signs of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245217/', '[1] "Product Information. Quadramet (samarium sm 153 lexidronam)." Berlex Laboratories  (2001):[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):', 1767365210);
