INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237618, 'Ropivacaine', 'Pazopanib', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ropivacaine.  Although ropivacaine is primarily metabolized by CYP450 1A2, it has been shown to undergo some metabolism via CYP450 3A4.', NULL, 'CYP450 3A4 inhibitors have a significant effect on the pharmacokinetics of ropivacaine only in the presence of CYP450 1A2 inhibitors such as fluvoxamine, ciprofloxacin, or mexiletine.', NULL, 'CYP450 3A4 inhibitors have a significant effect on the pharmacokinetics of ropivacaine only in the presence of CYP450 1A2 inhibitors such as fluvoxamine, ciprofloxacin, or mexiletine. Therefore, the combination of ropivacaine and the above drugs is not recommended.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245415/', '[1] Halldin MM, Bredberg E, Angelin B, Arvidsson T, Askemark Y, Elofsson S, Widman M "Metabolism and excretion of ropivacaine in humans." Drug Metab Dispos 24 (1996):  962-8[2] Oda Y, Furuichi K, Tanaka K, Hiroi T, Imaoka S, Asada A, Fujimori M, Funae Y "Metabolism of a new local anesthetic, ropivacaine, by human hepatic cytochrome P450." Anesthesiology 82 (1995):  214-20[3] "Product Information. Naropin (ropivacaine)." Astra-Zeneca Pharmaceuticals  (2001):[4] McClure JH "Ropivacaine." Br J Anaesth 76 (1996):  300-7[5] Ekstrom G, Gunnarsson UB "Ropivacaine, a new amide-type local anesthetic agent, is metabolized by cytochromes P450 1A and 3A in human liver microsomes." Drug Metab Dispos 24 (1996):  955-61[6] Arlander E, Ekstrom G, Alm C, Carrillo JA, Bielenstein M, Bottiger Y, Bertilsson L, Gustafsson LL "Metabolism of ropivacaine in humans is mediated by CYP1A2 and to a minor extent by CYP3A4: An interaction study with fluvoxamine and ketoconazole as in vivo inhibitors." Clin Pharmacol Ther 64 (1998):  484-91[7] Jokinen MJ, Ahonen J, Neuvonen PJ, Olkkola KT "The effect of erythromycin, fluvoxamine, and their combination on the pharmacokinetics of ropivacaine." Anesth Analg 91 (2000):  1207-12', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237619, 'Sibutramine', 'Pazopanib', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.  Inhibitors such as ketoconazole, cimetidine, and erythromycin have caused moderate increases in the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of the active metabolites.  The data suggest that although the potential for such interaction exists, the magnitude appears to be small.  No special precaution appears necessary; however, dosage adjustments as well as clinical and laboratory monitoring may be appropriate in some cases when a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245434/', '[1] "Product Information. Meridia (sibutramine)." Knoll Pharmaceutical Company  (2001):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237620, 'Tivozanib', 'Pazopanib', 'Moderate', 'Coadministration with tivozanib may increase the plasma concentrations and therapeutic effects of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.  The proposed mechanism, based on in vitro data, is decreased clearance due to inhibition of BCRP-mediated intestinal efflux by tivozanib.', NULL, 'Caution and clinical monitoring are advised if tivozanib is prescribed with orally administered drugs that are sensitive substrates for BCRP, such as rosuvastatin.', NULL, 'Caution and clinical monitoring are advised if tivozanib is prescribed with orally administered drugs that are sensitive substrates for BCRP, such as rosuvastatin.  If coadministration is necessary, the manufacturer suggests separating the administration times by 2 or more hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245445/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Fotivda (tivozanib)." Aveo Pharmaceuticals, Inc.  (2021):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237621, 'Tolterodine', 'Pazopanib', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the plasma concentrations of tolterodine, which is partially metabolized by the isoenzyme.  The possibility of prolonged and/or increased pharmacologic effects of tolterodine should be considered.  Although tolterodine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of tolterodine.', NULL, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.  Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be advised to notify their physician if they experience an irregular heartbeat, severe blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, or GI upset.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245446/', '[1] "Product Information. Detrol (tolterodine)." Pharmacia and Upjohn  (2001):[2] Brynne N, Forslund C, Hallen B, Gustafsson LL, Bertilsson L "Ketoconazole inhibits the metabolism of tolterodine in subjects with deficient CYP2D6 activity." Br J Clin Pharmacol 48 (1999):  564-72', 1767365210);
