INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237642, 'Ulipristal', 'Pitolisant', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ulipristal acetate and its pharmacologically active metabolite.  Based on in vitro and pharmacokinetic data, ulipristal acetate is thought to be primarily metabolized by CYP450 3A4 to mono-demethylated and di-demethylated metabolites.', NULL, 'When used to prevent pregnancy, ulipristal acetate should not be coadministered with CYP450 3A4 inducers due to the potential for loss of therapeutic efficacy.', NULL, 'When used to prevent pregnancy, ulipristal acetate should not be coadministered with CYP450 3A4 inducers due to the potential for loss of therapeutic efficacy.  For patients who have used enzyme-inducing drugs within the past 4 weeks and are seeking emergency contraception, ulipristal acetate is not recommended and a non-hormonal method (i.e.  a copper intrauterine device (Cu-IUD)) should be considered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245539/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[3] "Product Information. Esmya (ulipristal)." Gedeon Richter (UK) Ltd  (2021):[4] "Product Information. EllaOne (ulipristal)." HRA Pharma UK & Ireland Ltd  (2021):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237643, 'Daclatasvir', 'Semaglutide', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/245544/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sovaldi (sofosbuvir)." Gilead Sciences  (2013):[4] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences  (2014):[5] "Product 