INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237907, 'Niraparib', 'Melphalan flufenamide', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib.', NULL, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', NULL, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.  Complete blood counts should be performed weekly for the first month, monthly for the next 11 months, and periodically thereafter.  Do not start niraparib until patients have recovered from hematological toxicity caused by previous chemotherapy (Grade 1 or less).  If hematological toxicities have not resolved within 4 weeks after interruption, discontinue niraparib and refer the patient to a hematologist for further investigations, including bone marrow analysis and blood sample for cytogenetics.  If MDS/AML is confirmed, discontinue niraparib.  Patients should be advised to contact their physician if they experience weakness, fatigue, fever, weight loss, infections, shortness of breath, unusual bleeding or bruising, or blood in urine or stool.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246387/', '[1] "Product Information. Zejula (niraparib)." Tesaro Inc.  (2017):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237908, 'Pralsetinib', 'Niraparib', 'Moderate', 'Coadministration with pralsetinib may increase the plasma concentrations of drugs that are P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic anion transporting polypeptide 1B1 (OATP1B1), OATP1B3, organic anion transporter 1 (OAT1), multidrug and toxin extrusion 1 (MATE1), and/or MATE2-K substrates.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with substrates of these transporters for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246402/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237909, 'Promazine', 'Niraparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib.', NULL, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', NULL, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.  Complete blood counts should be performed weekly for the first month, monthly for the next 11 months, and periodically thereafter.  Do not start niraparib until patients have recovered from hematological toxicity caused by previous chemotherapy (Grade 1 or less).  If hematological toxicities have not resolved within 4 weeks after interruption, discontinue niraparib and refer the patient to a hematologist for further investigations, including bone marrow analysis and blood sample for cytogenetics.  If MDS/AML is confirmed, discontinue niraparib.  Patients should be advised to contact their physician if they experience weakness, fatigue, fever, weight loss, infections, shortness of breath, unusual bleeding or bruising, or blood in urine or stool.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246403/', '[1] "Product Information. Zejula (niraparib)." Tesaro Inc.  (2017):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237910, 'Selpercatinib', 'Niraparib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter.  The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by selpercatinib.', NULL, 'Caution is advised when selpercatinib is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when selpercatinib is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy.', 'Metabolism, Absorption, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246424/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237911, 'Yellow Fever Vaccine', 'Niraparib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246438/', '[1] "Product Information. Meruvax II (rubella virus vaccine)." Merck & Co., Inc  (2022):[2] "Product Information. Attenuvax (measles virus vaccine)." Merck & Co., Inc  (2022):[3] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur  (2001):[4] Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):[5] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993):  1-18[6] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Co., Inc  (2002):[7] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011):  1495-7[8] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014):  161[9] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence.  https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf"   (2019):[10] "Product Information. DENGVAXIA (dengue vaccine)." sanofi pasteur  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237912, 'Varicella Zoster Vaccine (Recombinant)', 'Niraparib', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition.  Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (e.g., greater than or equal to 2 mg/kg/day or 20 mg/day of prednisone or equivalent for 14 consecutive days or more), or long-term topical or inhaled corticosteroids.', NULL, 'In general, the U.', NULL, 'In general, the U.S. Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.  However, higher dosages, more frequent boosters, and/or serological testing may be required in some cases.  Local guidelines and prescribing information for individual vaccines should be consulted.  For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease.  For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.  For SARS-CoV-2 (COVID-19) vaccines, vaccination should preferably be completed at least two weeks before initiation or resumption of immunosuppressive therapies; however, decisions to delay or temporarily withhold immunosuppressive therapy to complete COVID-19 vaccination should consider the individual''s risks relative to their underlying condition.  Additional shots and/or boosters may be appropriate depending on age, prior COVID-19 vaccine formulation(s) received, and other factors in individuals with moderate to severe immune compromise due to medical conditions or immunosuppressive medications or treatments (e.g., solid organ transplant recipients on immunosuppressive therapy; patients on active treatment for solid tumor and hematologic malignancies).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246439/', '[1] "Product Information. Fluzone (influenza virus vaccine, inactivated)." Connaught Laboratories Inc[2] "Product Information. Omnihib (haemophilus b conjugate vaccine (obsolete))." SmithKline Beecham[3] "Product Information. Havrix (hepatitis A adult vaccine)." SmithKline Beecham[4] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993):  1-18[5] "Product Information. Imovax Rabies (rabies vaccine, human diploid cell)." sanofi pasteur  (2022):[6] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc.  (2003):[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline  (2022):[9] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence.  https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf"   (2019):[10] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations.  https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chapter_6.pdf"   (2019):[11] CDC Centers for Disease Control and Prevention "Interim Clinical Considerations for Use of COVID-19 Vaccines Currently Approved or Authorized in the United States.  https://www.cdc.gov/vaccines/covid-19/clinical-considerations/interim-considerations-us.html"   (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237913, 'Botulinum toxin type A', 'Plazomicin', 'Major', 'The effects of botulinum toxin may be potentiated by neuromuscular blockers, aminoglycoside antibiotics, and other drugs that can interfere with neuromuscular transmission.  Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive neuromuscular blocking effects may occur when these agents are used together.', NULL, 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission.', NULL, 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission.  These drugs should be avoided in patients suspected of having botulinum toxin overdose or poisoning.  Because the effects of botulinum toxin products may spread from the area of injection, patients should be monitored closely for adverse effects such as asthenia, generalized muscle weakness, diplopia, blurred vision, ptosis, dysphagia, dysphonia, dysarthria, urinary incontinence and breathing difficulties, which can occur hours to weeks after injection.  Swallowing and breathing difficulties may be life-threatening or fatal.  Patients with peripheral motor neuropathic diseases, amyotrophic lateral sclerosis, or neuromuscular junctional disorders such as myasthenia gravis or Lambert-Eaton syndrome may be particularly susceptible.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246446/', '[1]