INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237935, 'Rifapentine', 'Ozanimod', 'Moderate', 'Coadministration with rifapentine may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C8, 2C9, and/or 3A4 isoenzymes.  The mechanism is accelerated clearance due to induction of these isoenzymes by rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever rifapentine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246559/', '[1] "Product Information. Priftin (rifapentine)." Hoechst Marion Roussel  (2001):[2] Thijssen HH, Flin ois JP, Beaune PH "Cytochrome P4502C9 is the principal catalyst of racemic acenocoumarol hydroxylation reactions in human liver microsomes." Drug Metab Dispos 28 (2000):  1284-90', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237936, 'Roflumilast', 'Ozanimod', 'Moderate', 'The concomitant use of roflumilast with immunosuppressive agents has not been adequately studied.  Roflumilast is a selective phosphodiesterase 4 (PDE4) inhibitor and a nonsteroidal anti-inflammatory agent.  In experimental models and in vitro studies, roflumilast has exhibited immunomodulatory effects.  Specifically, roflumilast and its active metabolite, roflumilast N-oxide, suppress the release of inflammatory mediators including leukotriene B4, reactive oxygen species, tumor necrosis factor alpha, interferon gamma, and granzyme B.  Roflumilast also reduces sputum neutrophils and attenuates influx of neutrophils and eosinophils into the airways.', NULL, 'According to the product labeling for roflumilast in some countries such as Canada and the U.', NULL, 'According to the product labeling for roflumilast in some countries such as Canada and the U.K., concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.g., in the treatment of COPD exacerbations) should be avoided if possible.  Due to a lack of clinical experience, treatment with roflumilast should not be initiated, or existing treatment should be stopped, in patients receiving immunosuppressive agents and in patients with severe acute infectious diseases, cancers (except basal cell carcinoma), or severe immunological diseases (e.g., HIV infection, multiple sclerosis, lupus erythematosus, progressive multifocal leukoencephalopathy).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246560/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Daxas (roflumilast)." Nycomed Inc  (2011):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237937, 'Sipuleucel-T', 'Ozanimod', 'Moderate', 'Neither the leukapheresis procedure nor sipuleucel-T have been studied for use in combination with chemotherapy or immunosuppressive agents (e.g., systemic corticosteroids).  Since sipuleucel-T is designed to stimulate the immune system, concurrent use of myelo- or immunosuppressive agents may alter the efficacy and/or safety of sipuleucel-T.', NULL, 'Patients should be carefully evaluated to determine whether it is medically appropriate to reduce or discontinue chemotherapy or immunosuppressive agents prior to treatment with sipuleucel-T.', NULL, 'Patients should be carefully evaluated to determine whether it is medically appropriate to reduce or discontinue chemotherapy or immunosuppressive agents prior to treatment with sipuleucel-T.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246580/', '[1] "Product Information. Provenge (sipuleucel-T)." Dendreon Corporation  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237938, 'Sorafenib', 'Ozanimod', 'Major', 'Due to the risk of bradycardia and atrioventricular (AV) block, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of ozanimod treatment in patients receiving drugs that prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval.  Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with ozanimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.  Advice from a cardiologist should be sought if treatment with ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients on concurrent therapy with QT prolonging drugs with a known risk of torsades de pointes or drugs that slow heart rate or AV conduction, or in patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246584/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237939, 'Talimogene laherparepvec', 'Ozanimod', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus.  Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246588/', '[1] "Product Information. Imlygic (talimogene laherparepvec)." Amgen USA  (2015):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237940, 'Ticagrelor', 'Ozanimod', 'Minor', 'Ventricular pauses and bradycardia, mostly asymptomatic, have been observed in clinical studies with ticagrelor.  Theoretically, the risk of symptomatic bradycardia may be increased during concomitant use of other agents known to induce bradycardia.', NULL, 'Ventricular pauses and bradycardia, mostly asymptomatic, have been observed in clinical studies with ticagrelor.', NULL, 'Ventricular pauses and bradycardia, mostly asymptomatic, have been observed in clinical studies with ticagrelor.  Theoretically, the risk of symptomatic bradycardia may be increased during concomitant use of other agents known to induce bradycardia.  However, in PLATO, a large, randomized, double-blind efficacy and safety comparison study with clopidogrel in patients with acute coronary syndromes, no evidence of clinically significant adverse interactions was observed during concomitant administration with one or more drugs known to induce bradycardia including beta-blockers (96%), the calcium channel blockers diltiazem and verapamil (33%), and digoxin (4%).', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246593/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals  (2011):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237941, 'Tivozanib', 'Ozanimod', 'Moderate', 'Coadministration with tivozanib may increase the plasma concentrations and therapeutic effects of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.  The proposed mechanism, based on in vitro data, is decreased clearance due to inhibition of BCRP-mediated intestinal efflux by tivozanib.', NULL, 'Caution and clinical monitoring are advised if tivozanib is prescribed with orally administered drugs that are sensitive substrates for BCRP, such as rosuvastatin.', NULL, 'Caution and clinical monitoring are advised if tivozanib is prescribed with orally administered drugs that are sensitive substrates for BCRP, such as rosuvastatin.  If coadministration is necessary, the manufacturer suggests separating the administration times by 2 or more hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246597/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Fotivda (tivozanib)." Aveo Pharmaceuticals, Inc.  (2021):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237942, 'Yellow Fever Vaccine', 'Ozanimod', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during ozanimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.  Ozanimod causes reversible sequestration of lymphocytes in lymphoid tissues.', NULL, 'The use of live attenuated vaccines should be avoided during and for up to 3 months after discontinuing ozanimod treatment.', NULL, 'The use of live attenuated vaccines should be avoided during and for up to 3 months after discontinuing ozanimod treatment.  If live attenuated vaccine immunizations are required, administration should occur at least 1 month prior to initiation of ozanimod.  Patients without a healthcare professional confirmed history of varicella or without documentation of a full course of vaccination against varicella-zoster virus (VZV) should be tested for antibodies to VZV before initiating treatment with ozanimod.  For antibody-negative patients, a full course of vaccination with varicella vaccine is recommended prior to treatment with ozanimod.  In patients who have recently been vaccinated, ozanimod therapy should be postponed for 4 weeks to allow the full effect of vaccination to occur.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246604/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237943, 'Varicella Zoster Vaccine (Recombinant)', 'Ozanimod', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines during ozanimod therapy is generally safe but may be associated with a diminished or suboptimal immunologic response.', NULL, 'Immune response to vaccines may be reduced during and for up to three months after discontinuation of ozanimod therapy.', NULL, 'Immune response to vaccines may be reduced during and for up to three months after discontinuation of ozanimod therapy.  In patients who have recently been vaccinated, ozanimod therapy should preferably be postponed for one month to allow the full effect of vaccination to occur.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246605/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation  (2020):', 1767365210);
