INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (237961, 'Zidovudine', 'Selumetinib', 'Moderate', 'Coadministration with selumetinib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of organic anion transporter 3 (OAT3).  The proposed mechanism, based on in vivo data, is decreased clearance due to selumetinib-mediated inhibition of OAT3 transporter.', NULL, 'Caution is advised if selumetinib is used concomitantly with drugs that are substrates of OAT3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if selumetinib is used concomitantly with drugs that are substrates of OAT3, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring of the OAT3 substrate should be considered whenever selumetinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/246656/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation  (2012):', 1767365210);
