INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238071, 'Samarium (153Sm) lexidronam', 'Axitinib', 'Major', 'Theoretical concerns exist that chemotherapeutic agents and other bone marrow depressants may potentiate the myelosuppressive effects of samarium sm 153 lexidronam.  In clinical trials, white blood cell and platelet counts decreased to a nadir of approximately 40% to 50% of baseline in 95% of patients within three to five weeks after administration of samarium sm 153 lexidronam, and tended to return to pretreatment levels by eight weeks.  The potential for additive bone marrow toxicity with myelotoxic treatments including chemotherapy or external beam radiation has not been studied.', NULL, 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks.', NULL, 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks.  Moreover, samarium sm 153 lexidronam should not be given after either of these treatments until there has been time for adequate marrow recovery.  Caution and close monitoring of bone marrow function are advisable if coadministration with other myelotoxic agents is required.  Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, unusual bleeding or bruising, or signs of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', 'Entrectinib, Pralsetinib, Dabrafenib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Larotrectinib, Vemurafenib, Lapatinib, More', 'Strontium chloride Sr-89', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/247129/', '[1] "Product Information. Quadramet (samarium sm 153 lexidronam)." Berlex Laboratories  (2001):[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):', 1767369485);
