INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238231, 'Carbamazepine', 'Selexipag', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or uridine diphosphate glucuronosyltransferase (UGT) 1A3 and/or 2B7 enzymes may reduce the plasma concentrations and therapeutic effects of selexipag and its active metabolite.', NULL, 'Caution and clinical monitoring should be considered whenever a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer is added to or withdrawn from therapy, and the selexipag dosage adjusted as necessary.', NULL, 'Caution and clinical monitoring should be considered whenever a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer is added to or withdrawn from therapy, and the selexipag dosage adjusted as necessary.  Some authorities recommend increasing the selexipag dose up to 2-fold when coadministered with rifampin (US).  Patients receiving selexipag with a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer should be monitored for reduced efficacy.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Cilostazol, Iloprost, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, Streptokinase, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/247470/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Uptravi (selexipag)." Actelion Pharmaceuticals US Inc  (2016):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238232, 'Idelalisib', 'Selexipag', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase exposure to the active metabolite of selexipag.  According to the prescribing information, selexipag is hydrolyzed to its active metabolite in the liver and intestine by carboxylesterases, and the active metabolite subsequently undergoes oxidative metabolism catalyzed primarily by CYP450 2C8 and to a lesser extent by CYP450 3A4.', NULL, 'Caution is advised when selexipag is prescribed with CYP450 2C8 inhibitors.', NULL, 'Caution is advised when selexipag is prescribed with CYP450 2C8 inhibitors.  Dosage adjustments as well as clinical and laboratory monitoring of selexipag may be appropriate whenever a CYP450 2C8 inhibitor is added to or withdrawn from therapy.  Patients should be monitored for potentially increased side effects such as diarrhea, nausea, vomiting, flushing, anemia, and decreased appetite.', 'Metabolism', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, More', 'Betrixaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/247478/', '[1] "Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Uptravi (selexipag)." Actelion Pharmaceuticals US Inc  (2016):', 1767369485);
