INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238414, 'Drospirenone', 'Pasireotide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of drospirenone, which is partially metabolized by the isoenzyme.', NULL, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.', NULL, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.g., renal insufficiency; coadministration with medications that can increase potassium such as aldosterone antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium-sparing diuretics, potassium supplements, and heparin).  Consider checking serum potassium levels during the first treatment cycle and periodically thereafter as indicated.', 'Metabolism', 'Ulipristal, Norethisterone, Norelgestromin, Etonogestrel, Megestrol acetate, Norgestimate, Etynodiol, Estradiol, Levonorgestrel, Medroxyprogesterone acetate, Desogestrel, More', 'Norethisterone, Nafarelin, Cetrorelix, Estradiol, Ganirelix, Relugolix, Gonadorelin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/248003/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Slynd (drospirenone)." Exeltis USA, Inc.  (2022):[3] "Product Information. Angeliq (drospirenone-estradiol)." Bayer Pharmaceutical Inc  (2017):[4] "Product Information. Slinda (drospirenone)." Besins Healthcare Australia Pty Ltd  (2021):[5] "Product Information. Angeliq 1/2 (drospirenone-estradiol)." Bayer Australia Limited  (2021):[6] "Product Information. Slynd (drospirenone)." Duchesnay Inc  (2021):[7] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb  (2022):[8] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Australia Pty Ltd  (2023):[9] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Pharmaceuticals Ltd  (2021):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238415, 'Indium In-111 pentetreotide', 'Pasireotide', 'Moderate', 'Somatostatin analogs may reduce the accumulation of indium In-111 pentetreotide radioactivity in gastro-entero-neuroendocrine tumors and carcinoid tumors.', NULL, 'Consideration should be given to temporarily suspending somatostatin analog therapy prior to the administration of indium In-111 pentetreotide.', NULL, 'Consideration should be given to temporarily suspending somatostatin analog therapy prior to the administration of indium In-111 pentetreotide.  If temporary withdrawal of the somatostatin analog is considered possible, some authorities propose a withdrawal period of three days prior to administration of indium In-111 pentetreotide.  Since withdrawal of the somatostatin analog may lead to rebound effects such as hypoglycemia (particularly in patients with insulinoma and carcinoid syndrome), patients should be monitored for any signs of withdrawal.', 'Distribution', 'Capromab pendetide, Gallium Ga-68 gozetotide, Fludeoxyglucose (18F)', 'Norethisterone, Nafarelin, Elagolix, Cetrorelix, Estradiol, Ganirelix, Relugolix, Gonadorelin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/248008/', '[1] "Product Information. Octreoscan (indium pentetreotide In-111)." Mallinckrodt Medical Inc  (2022):[2] "Product Information. Octreoscan (indium (In-111) pentetreotide)." Curium Pharma UK Ltd  (2020):', 1767369485);
