INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238420, 'Silodosin', 'Osilodrostat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of silodosin, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4.  Pharmacologic response to silodosin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the silodosin dosage adjusted as necessary.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.  Taking silodosin at bedtime may minimize the occurrence of orthostatic effects.  Patients should also avoid driving or operating hazardous machinery until they know how the medication affects them.', 'Metabolism', 'Terazosin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/248047/', '[1] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals  (2008):', 1767369485);
