        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1466721, 'Fosphenytoin', 'Nelfinavir', 'Moderate', 'Coadministration with nelfinavir may decrease the plasma concentrations of phenytoin. The proposed mechanism is nelfinavir induction of phenytoin metabolism via CYP450 2C9.', NULL, 'The potential for reduced therapeutic effects of phenytoin should be considered during coadministration with nelfinavir.', NULL, 'The potential for reduced therapeutic effects of phenytoin should be considered during coadministration with nelfinavir. Phenytoin serum levels and pharmacologic effects should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of nelfinavir in patients who are stabilized on their anticonvulsant regimen. No dosage adjustment for nelfinavir is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1466722, 'Fostamatinib', 'Nelfinavir', 'Major', 'Coadministration of fostamatinib with potent inhibitors of CYP450 3A4 may significantly increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. Increased exposure to R406 may result in increased risk of adverse effects such as diarrhea, hepatotoxicity, hypertension, and neutropenia.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
