        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1466770, 'Nitisinone', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of and exposure to, nitisinone. The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism of nitisinone, which has been shown in in vitro studies to be metabolized by this isoenzyme.', NULL, 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors. Pharmacologic response to nitisinone should be monitored whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the nitisinone dosage adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
