INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466852, 'Sonidegib', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sonidegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of sonidegib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', 'Concomitant use of sonidegib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466853, 'St. John''s Wort', 'Nelfinavir', 'Major', 'Coadministration with St. John''s wort may significantly reduce the plasma concentrations of HIV protease inhibitors and result in a potential loss of virologic response. The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St. John''s wort is considered contraindicated. The enzyme induction may last for up to 2 weeks after the last dose of St. John''s wort. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466854, 'Stiripentol', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways. Conversely, many of these inhibitors are also substrates of CYP450 1A2 and/or 3A4, and coadministration with stiripentol may alter their plasma concentrations as well. Stiripentol is both an inhibitor and inducer of CYP450 1A2 and 3A4 in vitro.', 'DDInter', 1767317476, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways. Patients should be monitored for potentially increased adverse effects of stiripentol such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behavior, neutropenia, and thrombocytopenia. In addition, clinical and laboratory monitoring may be appropriate for concomitant drugs whenever stiripentol is added to or withdrawn from therapy. Dosage adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466855, 'Sulfinpyrazone', 'Nelfinavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466856, 'Sunitinib', 'Nelfinavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme. Because sunitinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Alternative agents with no or minimal CYP450 3A4-inhibiting activity are recommended in patients treated with sunitinib. Some authorities recommend avoiding concomitant use of sunitinib during and for 2 weeks after treatment with itraconazole. A dose reduction for sunitinib to a minimum of 37.5 mg daily for gastrointestinal stromal tumor (GIST) and metastatic renal cell carcinoma (mRCC) or 25 mg daily for pancreatic neuroendocrine tumors (pNET) should be considered if it must be coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', 'Alternative agents with no or minimal CYP450 3A4-inhibiting activity are recommended in patients treated with sunitinib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466857, 'Suvorexant', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of suvorexant with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', 'Concomitant use of suvorexant with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466858, 'Tacrolimus', 'Nelfinavir', 'Major', 'Coadministration with protease inhibitors (PIs) may significantly increase the blood concentrations of tacrolimus. The proposed mechanism is PI inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of tacrolimus. Enhanced tacrolimus oral bioavailability due to inhibition of intestinal P-glycoprotein (P-gp) efflux transporter may also contribute.', 'DDInter', 1767317476, 'Caution is advised when tacrolimus is used with protease inhibitors (PIs). Dosage reduction and/or prolongation of the dosing interval for tacrolimus will likely be required. Tacrolimus blood levels and renal function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of PI therapy. Patients should be closely monitored for development of serious adverse effects such as nephrotoxicity, lymphoma and other malignancies, infections, diabetes, neurotoxicity (tremor, paraesthesia, encephalopathy, delirium, coma), hyperkalemia, QT prolongation, myocardial hypertrophy, and hypertension. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised when tacrolimus is used with protease inhibitors (PIs).', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466859, 'Tadalafil', 'Nelfinavir', 'Moderate', 'Coadministration with protease inhibitors (PIs) may increase the plasma concentrations of tadalafil. The mechanism is PI inhibition of tadalafil metabolism via CYP450 3A4. Although not specifically studied, other HIV protease inhibitors are expected to increase tadalafil exposure.', 'DDInter', 1767317476, 'Caution is advised if tadalafil is administered in combination with protease inhibitors. For the treatment of erectile dysfunction, the dosage of tadalafil should not exceed 10 mg once every 72 hours when used on an as-needed basis in patients receiving PI therapy. When given for once-daily use in the treatment of erectile dysfunction or benign prostatic hyperplasia, the maximum recommended dose is 2.5 mg. For the treatment of pulmonary arterial hypertension, tadalafil should be started at 20 mg once daily in patients who have been receiving ritonavir-boosted PI therapy for at least one week. The dosage may be increased to 40 mg once daily based upon individual tolerability. The use of tadalafil for PAH should be avoided during the initiation of ritonavir-boosted PI therapy. The manufacturers recommend that tadalafil be discontinued at least 24 hours prior to initiating the PIs. After at least one week, tadalafil may be resumed at 20 mg once daily, and the dosage increased to 40 mg once daily based upon individual tolerability. There is no need to discontinue tadalafil when initiating PI therapy that does not contain ritonavir as a pharmacokinetic booster, nor wait at least one week after PI therapy to start tadalafil. The tadalafil dosage should be initiated at or adjusted to 20 mg once daily when coadministered with nonritonavir-boosted PI regimens, then increased to 40 mg as tolerated. All patients treated with tadalafil should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Caution is advised if tadalafil is administered in combination with protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466860, 'Talazoparib', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of both efflux membrane transporters.', 'DDInter', 1767317476, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Distribution', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466861, 'Tasimelteon', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317476, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4. Patients should be monitored for excessive sedation and other side effects.', 'Metabolism', 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466862, 'Tazemetostat', 'Nelfinavir', 'Major', 'Coadministration with moderate or potent CYP450 3A4 inhibitors may significantly increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'DDInter', 1767317476, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration with a moderate CYP450 3A4 inhibitor is required and no other alternatives are possible, the dose of tazemetostat should be reduced as per manufacturer recommendations. After the inhibitor has been discontinued for 3 elimination half-lives, the tazemetostat dose that was taken prior to initiating the inhibitor may be resumed. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466863, 'Telithromycin', 'Nelfinavir', 'Moderate', 'Coadministration with telithromycin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by telithromycin.', 'DDInter', 1767317476, 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever telithromycin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466864, 'Telotristat ethyl', 'Nelfinavir', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', 'DDInter', 1767317476, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466865, 'Temsirolimus', 'Nelfinavir', 'Major', 'Coadministration of temsirolimus with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', 'DDInter', 1767317476, 'Concomitant use of temsirolimus with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of intravenous temsirolimus during and for 2 weeks after treatment with itraconazole (US). If coadministration is required, the manufacturer recommends reducing the temsirolimus dosage to 12.5 mg once a week. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the temsirolimus dosage is adjusted upward to the normally recommended dosage (i.e., 25 mg once a week) or the dosage used prior to initiation of the CYP450 3A4 inhibitor.', 'Metabolism', 'Concomitant use of temsirolimus with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466866, 'Terfenadine', 'Nelfinavir', 'Major', 'Coadministration with protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of astemizole and terfenadine. The mechanism is PI inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of both astemizole and terfenadine. Although the interaction has not been specifically studied with any of the available PIs, high plasma levels of astemizole and terfenadine (e.g., due to overdose or interaction with other potent 3A4 inhibitors such as macrolide antibiotics and azole antifungal agents) have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death.', 'DDInter', 1767317476, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, use of these agents in patients treated with protease inhibitors is considered contraindicated. Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with PIs.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, use of these agents in patients treated with protease inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466867, 'Testosterone (topical)', 'Nelfinavir', 'Moderate', 'Coadministration with nelfinavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nelfinavir.', 'DDInter', 1767317476, 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nelfinavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466868, 'Tiagabine', 'Nelfinavir', 'Moderate', 'Coadministration with nelfinavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nelfinavir.', 'DDInter', 1767317476, 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nelfinavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466869, 'Ticagrelor', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of ticagrelor with potent CYP450 3A4 inhibitors is considered contraindicated by some foreign authorities. The U.S. labeling for ticagrelor recommends avoiding use with potent CYP450 3A4 inhibitors. Labeling for itraconazole, a potent CYP450 3A4 inhibitor, specifically contraindicates use with ticagrelor during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of ticagrelor with potent CYP450 3A4 inhibitors is considered contraindicated by some foreign authorities.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466870, 'Tipranavir', 'Nelfinavir', 'Major', 'Coadministration with tipranavir plus low-dose ritonavir may significantly decrease the plasma concentrations of other protease inhibitors. The mechanism of interaction has not been described. Data are available for amprenavir, atazanavir, lopinavir, and saquinavir.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of other protease inhibitors in combination with tipranavir/ritonavir is not recommended.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of other protease inhibitors in combination with tipranavir/ritonavir is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466871, 'Tofacitinib', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tofacitinib, which is metabolized by the isoenzyme. Side effects including lymphopenia, neutropenia, anemia, serious infections, and hyperlipidemia may be increased.', 'DDInter', 1767317476, 'The dosage of tofacitinib should be reduced by 50% when used with potent CYP450 3A4 inhibitors. For example, the dose for patients receiving 10 mg twice daily should be reduced to 5 mg twice daily and the dose for patients receiving 5 mg twice daily should be reduced to 5 mg once daily. For patients receiving 11 mg once daily of the extended-release formulation, the dose should be reduced to 5 mg once daily of the immediate-release formulation. The dose for patients receiving 3.2 mg twice daily should be reduced to 3.2 mg once daily and the dose for patients receiving 4 mg twice daily should be reduced to 4 mg once daily.', 'Metabolism', 'The dosage of tofacitinib should be reduced by 50% when used with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466872, 'Tolazamide', 'Nelfinavir', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317476, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466873, 'Tolbutamide', 'Nelfinavir', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317476, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466874, 'Tolterodine', 'Nelfinavir', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the plasma concentrations of tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tolterodine should be considered. Although tolterodine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of tolterodine.', 'DDInter', 1767317476, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience an irregular heartbeat, severe blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, or GI upset.', 'Metabolism', 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466875, 'Toremifene', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of toremifene, which is primarily metabolized by the isoenzyme. Because toremifene is associated with dose- and concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317476, 'The use of toremifene in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. If treatment with one of these agents is required, interruption of toremifene therapy should be considered. Otherwise, patients who require concomitant treatment should be closely monitored. In patients at increased risk, electrocardiograms (ECGs) should be obtained at baseline and as clinically indicated. Complete blood counts, electrolyte levels (calcium, magnesium, potassium), and liver function tests should also be obtained periodically. Patients should be advised to contact their physician if they experience vaginal bleeding or potential signs of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity. Patients should seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'The use of toremifene in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466876, 'Trabectedin', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of trabectedin during and for 2 weeks after treatment with itraconazole. If a potent CYP450 3A4 inhibitor is required for short-term use (i.e., less than 14 days), it is advisable to administer the CYP450 3A4 inhibitor one week after the trabectedin infusion and discontinue it the day prior to the next infusion. Close monitoring for toxicities such as myelosuppression, rhabdomyolysis, hepatotoxicity, and cardiomyopathy is recommended during long-term coadministration, and the trabectedin dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466877, 'Tretinoin', 'Nelfinavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 2C8, 2C9, and/or 3A4 may increase the plasma concentrations and toxicities of tretinoin, which is a substrate of these isoenzymes. There have been isolated reports of pseudotumour cerebri, hypercalcemia, and acute renal failure in patients receiving tretinoin with fluconazole, itraconazole or voriconazole, all of which are considered potent inhibitors of at least one CYP450 isoenzyme involved in the metabolism of tretinoin. The conditions resolved following interruption of tretinoin therapy and/or discontinuation of the azole antifungal agent.', 'DDInter', 1767317476, 'Caution is advised when tretinoin is prescribed in combination with potent inhibitors of CYP450 2C8, 2C9, and/or 3A4. Patients should be closely monitored and advised to seek medical attention immediately if they develop early symptoms of pseudotumour cerebri such as headache, nausea, vomiting, visual disturbances, photosensitivity, and tinnitus.', 'Metabolism', 'Caution is advised when tretinoin is prescribed in combination with potent inhibitors of CYP450 2C8, 2C9, and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466878, 'Triamcinolone (nasal)', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of corticosteroids, which are primarily metabolized by the isoenzyme. The interaction has been reported with potent inhibitors such as clarithromycin, erythromycin, itraconazole, nefazodone, cobicistat, and ritonavir during concomitant use of various corticosteroids, including inhaled, nasal, and ophthalmic formulations. Systemic corticosteroid adverse effects may occur following intensive or long-term continuous ophthalmic corticosteroid therapy. Cushing''s syndrome and adrenal insufficiency have been attributed to the interaction.', 'DDInter', 1767317476, 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors. When indicated for intranasal or inhalational use, alternative corticosteroids such as beclomethasone, which is less dependent on CYP450 3A4 metabolism, should be considered, particularly if long term treatment is required. Patients should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Following extensive use with a potent CYP450 3A4 inhibitor, a progressive dosage reduction may be required over a longer period if the corticosteroid is to be withdrawn from therapy, as there may be a significant risk of adrenal suppression.', 'Metabolism', 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466879, 'Triamcinolone (ophthalmic)', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of corticosteroids, which are primarily metabolized by the isoenzyme. The interaction has been reported with potent inhibitors such as clarithromycin, erythromycin, itraconazole, nefazodone, cobicistat, and ritonavir during concomitant use of various corticosteroids, including inhaled, nasal, and ophthalmic formulations. Systemic corticosteroid adverse effects may occur following intensive or long-term continuous ophthalmic corticosteroid therapy. Cushing''s syndrome and adrenal insufficiency have been attributed to the interaction.', 'DDInter', 1767317476, 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors. When indicated for intranasal or inhalational use, alternative corticosteroids such as beclomethasone, which is less dependent on CYP450 3A4 metabolism, should be considered, particularly if long term treatment is required. Patients should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Following extensive use with a potent CYP450 3A4 inhibitor, a progressive dosage reduction may be required over a longer period if the corticosteroid is to be withdrawn from therapy, as there may be a significant risk of adrenal suppression.', 'Metabolism', 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466880, 'Triazolam', 'Nelfinavir', 'Major', 'Coadministration with protease inhibitors (PIs) may significantly increase the plasma concentrations and pharmacologic effects of orally administered midazolam and triazolam. The mechanism is PI inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of these benzodiazepines. Within the class, ritonavir is the most potent CYP450 3A4 inhibitor, while saquinavir is considered one of the weaker ones.', 'DDInter', 1767317476, 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated. Caution and close clinical monitoring are recommended when administering parenteral midazolam in combination with protease inhibitors. Appropriate medical management should be readily available in case of respiratory depression and/or prolonged sedation. Dosage adjustment for midazolam may be appropriate, especially if more than a single dose of midazolam is administered.', 'Metabolism', 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466881, 'Troglitazone', 'Nelfinavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
