INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466883, 'Tucatinib', 'Nelfinavir', 'Moderate', 'Coadministration with tucatinib may increase the plasma concentrations of CYP450 3A4 substrates. The mechanism involves tucatinib-mediated inhibition of CYP450 3A isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when tucatinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever tucatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when tucatinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466884, 'Ubrogepant', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of potent CYP450 3A4 inhibitors is contraindicated with ubrogepant.', 'Metabolism', 'Concomitant use of potent CYP450 3A4 inhibitors is contraindicated with ubrogepant.', NULL, NULL, 'Major', 'pharmacodynamic');
