INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1466969, 'Torasemide', 'Neomycin', 'Major', 'Coadministration of parenteral or inhaled aminoglycoside antibiotics or oral neomycin in combination with loop diuretics may potentiate the risk of oto- or nephrotoxicity due to additive or synergistic pharmacologic effects of these drugs and or altered aminoglycoside serum and tissue levels. Coadministration of aminoglycosides with intravenous mannitol may increase the risk of nephrotoxicity. The risk may be greater with high dosages of either drug, preexisting renal insufficiency, advanced age, dehydration, or the presence of other oto- or nephrotoxic drugs. The onset of ototoxicity may be greatly delayed, and cochlear damage may initially be asymptomatic.', 'DDInter', 1767317476, 'The use of aminoglycoside antibiotics in combination with loop diuretics or intravenous mannitol should generally be avoided. Renal function tests and serial, vestibular, and audiometric tests should be performed as appropriate for the type of diuretic before and during therapy if coadministration is necessary.', 'Synergism', 'The use of aminoglycoside antibiotics in combination with loop diuretics or intravenous mannitol should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
