INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1467007, 'Siponimod', 'Neostigmine', 'Major', 'The risk of severe bradycardia and atrioventricular (AV) block may be increased during initiation of siponimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis.', 'DDInter', 1767317476, 'Siponimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. Treatment with siponimod should generally not be initiated in patients who are concurrently treated with heart rate-lowering drugs. Advice from a cardiologist should be sought if coadministration of siponimod and drugs that slow heart rate or AV conduction is considered.', 'Synergism', 'Siponimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, NULL, 'Major', 'pharmacodynamic');
