INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1467059, 'Aluminum hydroxide', 'Neratinib', 'Moderate', 'Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of neratinib. The solubility of neratinib is pH-dependent and increases as neratinib becomes protonated at acidic pH, thus an increase in pH may interfere with its absorption.', 'DDInter', 1767317476, 'Concomitant use of neratinib with drugs that increase gastric pH should generally be avoided. If acid-suppression therapy is required, short-acting antacids should be considered, with neratinib dosing separated by at least three hours after antacid administration.', 'Absorption', 'Concomitant use of neratinib with drugs that increase gastric pH should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
