INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1467061, 'Neratinib', 'Artesunate', 'Moderate', 'Coadministration with neratinib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter, such as dabigatran, rosuvastatin, sulfasazine, and topotecan. The proposed mechanisms, based on in vitro data, is decreased clearance due to neratinib-mediated inhibition of BCRP transport protein. There are no clinical data regarding the use of neratinib with BCRP substrates.', 'DDInter', 1767317476, 'Caution is advised if neratinib is used concomitantly with drugs that are substrates of BCRP transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever neratinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Others', 'Caution is advised if neratinib is used concomitantly with drugs that are substrates of BCRP transport protein, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1467062, 'Berotralstat', 'Neratinib', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat. Berotralstat is a substrate of both P-gp and BCRP.', 'DDInter', 1767317476, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors. Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Absorption', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
