INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1467093, 'Niraparib', 'Neratinib', 'Moderate', 'Coadministration with neratinib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter, such as dabigatran, rosuvastatin, sulfasazine, and topotecan. The proposed mechanisms, based on in vitro data, is decreased clearance due to neratinib-mediated inhibition of BCRP transport protein. There are no clinical data regarding the use of neratinib with BCRP substrates.', 'DDInter', 1767317476, 'Caution is advised if neratinib is used concomitantly with drugs that are substrates of BCRP transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever neratinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Others', 'Caution is advised if neratinib is used concomitantly with drugs that are substrates of BCRP transport protein, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
