INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238547, 'Fostemsavir', 'Somapacitan', 'Minor', 'Coadministration of fostemsavir with moderate or weak CYP450 3A4 inducers may decrease the plasma concentrations of temsavir, the active moiety of fostemsavir.', NULL, 'Coadministration of fostemsavir with moderate or weak CYP450 3A4 inducers may decrease the plasma concentrations of temsavir, the active moiety of fostemsavir.', NULL, 'Coadministration of fostemsavir with moderate or weak CYP450 3A4 inducers may decrease the plasma concentrations of temsavir, the active moiety of fostemsavir.  According to the prescribing information, temsavir is a substrate of CYP450 3A4, esterases, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).  When fostemsavir (600 mg twice daily) was coadministered with the moderate CYP450 3A4 inducer etravirine (200 mg twice daily) in 14 study subjects, mean temsavir peak plasma concentration (Cmax), systemic exposure (AUC) and trough plasma concentration (Ctau) decreased by 48%, 50% and 52%, respectively, compared to fostemsavir administered alone.  When the same dosage of fostemsavir was given to 22 study subjects with another moderate CYP450 3A4 inducer, rifabutin (300 mg once daily), mean temsavir Cmax, AUC and Ctau decreased by 27%, 30% and 41%, respectively.  These changes are not considered clinically relevant, and no dosage adjustment of fostemsavir is recommended when coadministered with moderate or weak CYP450 3A4 inducers.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/249049/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Rukobia (fostemsavir)." ViiV Healthcare  (2020):', 1767363027);
