INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238617, 'Human botulinum neurotoxin A/B immune globulin', 'Moxetumomab Pasudotox', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).  The manufacturers recommend administering immune globulin infusions at the minimum concentration available and at the minimum rate of infusion feasible in such patients.  Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy.  Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.  If renal function deteriorates, discontinuation of the product should be considered.  Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/249462/', '[1] "Product Information. Cytogam (cytomegalovirus immune globulin)." CSL Behring LLC  (2002):[2] "Product Information. Respigam (respiratory syncytial virus immune globulin)." Medimmune Inc  (2005):[3] "Product Information. BabyBIG (botulism immune globulin)." FFF Enterprises  (2008):[4] "Product Information. Bivigam (immune globulin intravenous)." Biotest Pharmaceuticals Corporation  (2013):[5] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998.  http://www.cdc.gov/mmwr/preview/mmwrhtml/mm4824a3.htm"   (2013):[6] Pusey EY, Levy JB "Nephrotoxicity of intravenous immunoglobulin.  http://qjmed.oxfordjournals.org/content/93/11/751.full.pdf+html"   (2013):[7] KDIGO. Kidney Disease Improving Global Outcomes "KDIGO clinical practice guideline for acute kidney injury.  http://www.kdigo.org/clinical_practice_guidelines/pdf/KDIGO-AKI-Suppl-Appendices-A-F_March2012.pdf"   (2013):[8] Guo X, Nzerue C "How to prevent, recognize, and treat drug-induced nephrotoxicity.  http://www.ccjm.org/content/69/4/289.full.pdf"   (2013):[9] Perazella MA "Renal vulnerability to drug toxicity.  http://www.ccjm.org/content/69/4/289.full.pdf"   (2013):[10] Naughton CA "Drug-induced nephrotoxicity.  http://www.aafp.org/afp/2008/0915/p743.html"   (2013):[11] Moses S "Nephrotoxic drugs, drug-induced nephrotoxicity.  http://www.fpnotebook.com/Renal/Pharm/NphrtxcDrgs.htm"   (2013):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238618, 'Human botulinum neurotoxin A/B immune globulin', 'Yellow Fever Vaccine', 'Moderate', 'The passive transfer of antibodies present in immune globulin preparations may interfere with the immune response to live vaccines.', NULL, 'Varicella virus vaccine should not be given for at least 5 months, and other live vaccines should not be given for at least 3 months, after administration of immune globulin.', NULL, 'Varicella virus vaccine should not be given for at least 5 months, and other live vaccines should not be given for at least 3 months, after administration of immune globulin.  If such vaccinations are given shortly before or after immune globulin therapy, a revaccination may be necessary.  Some experts have recommended that administration of measles virus vaccine be deferred for at least 8 months following human intravenous immune globulin therapy for immunodeficiency disease; 8 to 10 months following treatment for immune thrombocytopenic purpura (ITP); and at least 11 months following treatment for Kawasaki syndrome.  In the event that immune globulin and measles virus vaccine must be administered concurrently, it is advisable to separate the injection sites by as much as possible and to administer an additional dose of vaccine at the appropriate specified interval.  An additional dose of live measles, mumps, or rubella vaccine is usually not needed if the vaccine was administered at least two weeks prior to immune globulin administration.  Hepatitis B immune globulin has been reported to not interfere with measles vaccination, although data are limited.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddin