INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238673, 'Amikacin (liposome)', 'Amikacin', 'Moderate', 'Use of more than one aminoglycoside concurrently or sequentially may increase the risk of nephrotoxicity and ototoxicity.', NULL, 'The use of more than one aminoglycoside should be avoided.', NULL, 'The use of more than one aminoglycoside should be avoided.  Some authorities consider coadministration of amikacin liposome inhalation with any aminoglycoside administered via any route to be contraindicated.  Patients receiving aminoglycosides should be closely monitored for development of toxicity, including renal function tests and serial, vestibular, and audiometric tests.  Ototoxicity may be permanent and may not be apparent until long after therapy has been discontinued.  The onset of auditory toxicity may be asymptomatic or may manifest with early symptoms such as feelings of fullness in the ear, high tone hearing loss, or tinnitus.  Signs of vestibulotoxicity include ataxia, tinnitus, and vertigo.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/249907/', '[1] Halstenson CE, Hirata CA, Heim-Duthoy KL, Abraham PA, Matzke GR "Effect of concomitant administration of piperacillin on the dispositions of netilmicin and tobramycin in patients with end-stage renal disease." Antimicrob Agents Chemother 34 (1990):  128-33[2] Weibert R, Keane W, Shapiro F "Carbenicillin inactivation of aminoglycosides in patients with severe renal failure." Trans Am Soc Artif Intern Organs 22 (1976):  439-43[3] Kaka JS, Lyman C, Kilarski DJ "Tobramycin-furosemide interaction." Drug Intell Clin Pharm 18 (1984):  235-8[4] Dupuis JY, Martin R, Tetrault JP "At