INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238895, 'Heparin (flush)', 'Bivalirudin', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', NULL, 'Caution and monitoring (e.', NULL, 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/250702/', '[1] Passannante A, Macik BG "The heparin flush syndrome: a cause of iatrogenic hemorrhage." Am J Med Sci 296 (1988):  71-3[2] "Product Information. Heparin Sodium in Sodium Chloride (heparin flush)." Baxter Healthcare Corporation  (2022):[3] "Product Information. Canusal (heparin flush)." Wockhardt UK Ltd  (2018):[4] "Product Information. Heparinised Saline (Pfizer) (heparinised saline)." Pfizer Australia Pty Ltd  (2022):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238896, 'Enoxacin', 'Meloxicam', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use.  The interaction has been reported most often with enoxacin.  It may occur with other fluoroquinolones as well, but is poorly documented.  The exact mechanism of interaction is unknown.  Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect.  Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/250867/', '[1] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986):  187-93[2] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985):  716-21[3] "Product Information. Cipro (ciprofloxacin)." Bayer  (2002):[4] "Product Information. Penetrex (enoxacin)." Rhone Poulenc Rorer  (2002):[5] "Product Information. Floxin (ofloxa