INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238964, 'Dolasetron', 'Pralsetinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251278/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238965, 'Aprepitant', 'Apalutamide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', NULL, 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St.', NULL, 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St. John''s wort.  Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251282/', '[1] "Product Information. Emend (aprepitant)." Merck & Co., Inc  (2003):[2] "Product Information. Emend for Injection (fosaprepitant)." Merck & Co., Inc  (2008):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238966, 'Betamethasone', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations of drugs that are substrates of the metabolic enzymes CYP450 3A4, CYP450 2C19, CYP450 2C9, and uridine diphosphate glucuronosyltransferase (UGT), as well as substrates of the membrane transporters organic anion transporting polypeptide (OATP) 1B1, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.  Substitution for these medications is recommended when possible, or monitor for potential loss of therapeutic efficacy if coadministration is required.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration of a CYP450 inducer like apalutamide and for any dosage adjustments that may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251287/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Erleada (apalutamide)." Janssen Biotech, Inc.  (2018):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (238967, 'Brivaracetam', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 2C19 may decrease the plasma concentrations of brivaracetam.  According to the prescribing information, brivaracetam undergoes hydroxylation mediated primarily by CYP450 2C19, although it appears to be a relatively minor, secondary biotransformation pathway.', NULL, 'Dosage adjustments for brivaracetam should be considered when coadministered with potent CYP450 2C19 inducers and following their discontinuation.', NULL, 'Dosage adjustments for brivaracetam should be considered when coadministered with potent CYP450 2C19 inducers and following their discontinuation.  The manufacturer recommends increasing the brivaracetam dosage by up to 100% (i.e., doubling the dosage) when prescribed with rifampin.  Clinical response and tolerability should be monitored closely.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251289/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] "Product Information. Briviact (brivaracetam)." UCB Pharma Inc  (2016):', 1767363027);
