INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1467094, 'Nizatidine', 'Neratinib', 'Major', 'Coadministration with proton pump inhibitors or H2-receptor antagonists may significantly decrease the oral bioavailability of neratinib. The solubility of neratinib is pH-dependent and increases as neratinib becomes protonated at acidic pH, thus an increase in pH may interfere with its absorption.', NULL, 'Concomitant use of neratinib with proton pump inhibitors and H2-receptor antagonists should generally be avoided.', NULL, 'Concomitant use of neratinib with proton pump inhibitors and H2-receptor antagonists should generally be avoided. In addition, short-acting antacids could be considered if acid-suppression therapy is required, with neratinib dosing separated by at least three hours after antacid administration. If coadministered with a gastric acid reducing agent, increasing the dose of neratinib is unlikely to compensate for the loss of bioavailability.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1467095, 'Norgestrel', 'Neratinib', 'Moderate', 'RECOMMENDED: Coadministration with neratinib may reduce the efficacy of hormonal contraceptives. The mechanism for the potential interaction has not been established.', NULL, 'Although the clinical significance of this interaction is unknown, caution is advised when neratinib is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.', NULL, 'Although the clinical significance of this interaction is unknown, caution is advised when neratinib is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm. Women should avoid becoming pregnant during treatment with neratinib and for up to 1 month after discontinuing treatment. Women using hormonal contraceptives should add a barrier method. In addition, men receiving neratinib should use a barrier method of contraception during treatment and for 3 months after discontinuing treatment.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1467096, 'Osimertinib', 'Neratinib', 'Moderate', 'Coadministration with neratinib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter, such as dabigatran, rosuvastatin, sulfasazine, and topotecan. The proposed mechanisms, based on in vitro data, is decreased clearance due to neratinib-mediated inhibition of BCRP transport protein. There are no clinical data regarding the use of neratinib with BCRP substrates.', NULL, 'Caution is advised if neratinib is used concomitantly with drugs that are substrates of BCRP transport protein, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if neratinib is used concomitantly with drugs that are substrates of BCRP transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever neratinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
